import Head from 'next/head'
import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'
import PowerstatsContainer from '../../components/PowerstatsContainer'

import Error from '../_error'

const Character = ({statusCode, data}) => {
  

  if (statusCode === 404) {
    return <Error statusCode={statusCode}/>
  }

  const name = data.name.toUpperCase();
  const fullName = data.biography.fullName.toUpperCase();
  const publisher = data.biography.publisher.toUpperCase();

  return (
    <>
    <Layout>
    <Head>
      <title>{name}</title>
    </Head>
  
  <div className="row">
  <div className="col-6">
  <h1>{name}</h1>
  <PowerstatsContainer stats={data.powerstats}/>
  </div>
  <div className="col-6 d-flex justify-content-center">
  <img src={data.images.md}/>
  </div>
  
  
  
  </div>
  </Layout>
  </>
  )

  

}

