import Powerstat from './Powerstat'
import NodeGroup from 'react-move/NodeGroup'



const data = [
  {
    id: 1,
    value: 23,
    name: 'Item 1'
  },
  {
    id: 2,
    value: 45,
    name: 'Item 2'
  },{
    id: 3,
    value: 69,
    name: 'Item 3'
  },
]

