import { useRouter } from 'next/router'
import Head from 'next/head'
import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'
import PowerstatsContainer from '../../components/PowerstatsContainer'


const Hero = ({heroData}) => {
  const router = useRouter()
  const { slug } = router.query
  // console.log(slug.split('-', 1))
  // const id = slug.split('-', 1)[0]
  
  return (
    <>
    <Layout>
    <Head>
      <title>{heroData.name}</title>
    </Head>
  {/* <p>{slug}</p> */}
  <PowerstatsContainer stats={heroData.powerstats}/>
  </Layout>
  </>
  )
}

export async function getStaticProps({ params }) {
  const res = await fetch(`https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${params.id}.json`)
  const heroData = await res.json()
  return {
    props: {
      heroData,
    }
  }
}

export async function getStaticPaths() {

  const res = await fetch('https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/all.json')
  const heroData = await res.json()

  const paths = heroData.map(hero => ({params: {id: hero.id}}))

  return {
    paths,
    fallback: false
  }
}

export default Hero
