const PowerBar = ({name, width, percent}) => {

  const [value, setValue] = React.useState(0)

  React.useEffect(() => {
    setValue(percent/100 * width);
  });

  let color;
  const getColor = (name) => {
    if (name === 'intelligence') {
      color = 'blue'
    }
  }

  getColor(name)

  return (
    <>
    <div className="d-flex">
    {name.toUpperCase()}
    <div>
    <div className="power-bar" style={{width: width}}>
      <div className="level" style={{width: `${value}px`, backgroundColor: 'red'}}></div>
      
    </div>
    </div>
    </div>
    </>
  )
}

export default PowerBar
