import Link from "next/link";
import { TextFit } from 'react-textfit'

const CharacterCard = ({ data }) => {

  const name = data.name.toUpperCase()
  const fullName = data.biography.fullName.toUpperCase()
  const publisher = data.biography.publisher.toUpperCase()
  
  return (
    <>
      {/* <Link href="/characters/[id]" as="/characters/502-one-punch-man"> */}
      {/* <div className="characterCard" 
    style={{ 
      backgroundImage: `url(${data.images.sm})`,
      
      }}> */}
      <div className="characterCard">
        <div className="characterImageContainer">
          
            <img
              src={data.images.sm}
              alt={data.name}
              className="characterImage"
              
            />
          
        </div>
       
        <div className="info">
        <p className="pl-2 name">{name}</p>
        <p className="pl-2 text-muted full-name">{name === fullName ? '' : fullName}</p>
        {/* <p className="pl-2 text-muted"> */}
        <TextFit mode="multi">
        {publisher}asdasdasdasdasd
        </TextFit>
        {/* </p> */}
        </div>
      </div>
      {/* </div> */}
      {/* </Link> */}
    </>
  );
};

export default CharacterCard;
