import CharacterCard from "../components/CharacterCard";
import { idList } from "../public/static/js/idList";
import FilterResults from "react-filter-search";
import { useState } from 'react'
// console.log(idList)
let currData = [];
// let num = 30
//no 501 entry
const Index = ({ data }) => {
  // console.log(data)

  const [page, setPage] = useState(0)

  data.slice(page,18*page).map((character) => {
    console.log(character.name)
  })


  return (
    <>
      <Layout>
        {/* <Link href="/characters/[slug]" as="/characters/502-one-punch-man"><a>One Punch Man</a></Link> */}
        <div className="row no-gutters">
          <div className="col"></div>
          <div className="col-10 p-0">
            <div className="cardGrid">
              {/* {data.splice(2).map((character) => (
                <CharacterCard data={character} />
              ))} */}
              {/* <CharacterCard data={data[1]}/>
              <CharacterCard data={data[0]}/> */}
            </div>
          </div>
          <div className="col p-0"></div>
        </div>
      </Layout>
    </>
  );
};

async function getInitialProps({ query, res }) {
  currData = [];

  try {
    let resp = await fetch(
      // `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/502.json`
      `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/all.json`
    );
    let data = await resp.json();

    return {
      data,
      statusCode: true ? 200 : 404,
    };


    // currData.push(data);
  } catch (error) {
    console.log("Error!");
  }

  // for (let i = 1; i < 2; i++) {

  //   try {
  //     let resp = await fetch(
  //       `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${i}.json`
  //       // `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/all.json`
  //     );
  //     let data = await resp.json();

  //     currData.push(data)

  //   } catch (error) {
  //     console.log(`Error! on ${i}`)
  //   }

  // }

  // if (query.slug === data.slug) {
  //   return { data, statusCode: 200 };
  // }
  // res.statusCode = 404;
  // return { data: {}, statusCode: 404 };
  return {
    data: currData,
    statusCode: true ? 200 : 404,
  };
}

Index.getInitialProps = getInitialProps;

export default Index;
