import { useRouter } from 'next/router'
import Head from 'next/head'
import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'
import PowerstatsContainer from '../../components/PowerstatsContainer'


const Hero = ({character}) => {
  const router = useRouter()
  const { slug } = router.query
  // console.log(slug.split('-', 1))
  // const id = slug.split('-', 1)[0]
  
  return (
    <>
    <Layout>
    <Head>
      <title>{character.name}</title>
    </Head>
  {/* <p>{slug}</p> */}
  <h1>{character.name}</h1>
  <PowerstatsContainer stats={character.powerstats}/>
  </Layout>
  </>
  )
}

export async function getStaticPaths({ params }) {

  // const res = await fetch('https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/all.json')
  const res = await fetch(`https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${params.id}.json`)

  const characters = await res.json()

  const paths = characters.map(hero => ({
    params: {id: hero.id.toString()}
  }))
  
  // const paths = {
  //   params: {"slug": slug}
  // }
  return { paths, fallback: false }
}

export async function getStaticProps({ params }) {
  const res = await fetch(`https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${params.id}.json`)
  const character = await res.json()

  return { props: { character } }
}

export default Hero
