// import ReactImageAppear from 'react-image-appear';

import ImageLoader from 'react-loading-image'
import Link from "next/link";
import _ from 'lodash'
import { Textfit } from 'react-textfit'

const CharacterCard = ({ data }) => {
  const name = data.name;
  const fullName = data.biography.fullName;
  const publisher = data.biography.publisher;

  return (
    <>
      <Link href="/characters/[slug]" as={`/characters/${data.slug}`}>
        <div className="characterCard">
          <div className="characterImageContainer">
            {/* <img
              src={data.images.sm}
              alt={data.name}
              className="characterImage"
              
            /> */}

            <ImageLoader
              src={data.images.sm}
              alt={data.name}
              className="characterImage"
              loading={() => <div className="lds-dual-ring d-flex justify-content-center align-items-center h-100 mx-auto"></div>}
              error={() => <div className="d-flex justify-content-center align-items-center h-100">Error</div>}
            />

          </div>
          <div className="info d-flex flex-column align-items-start">
            <p className="pl-2 name animated fadeIn">{_.upperCase(name)}</p>
            <p className="pl-2 text-muted full-name animated fadeIn">
              {name === fullName ? "" : fullName}
            </p>
            {/* <p className="pl-2 text-muted animated fadeIn mt-auto">{publisher ? _.upperCase(publisher) : ""}</p> */}
            
            <Textfit mode="single">
            <p className="pl-2 text-muted animated fadeIn mt-auto">{publisher ? _.upperCase(publisher) : ""}</p>
            </Textfit>
          
          
          </div>
        </div>
      </Link>
    </>
  );
};

export default CharacterCard;
