import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'
import PowerstatsContainer from '../../components/PowerstatsContainer'
import _ from 'lodash'

import Error from '../_error'

const Character = ({statusCode, data}) => {
  

  if (statusCode === 404) {
    return <Error statusCode={statusCode}/>
  }

  const name = data.name.toUpperCase();
  const biography = data.biography
  const fullName = data.biography.fullName.toUpperCase();
  const publisher = data.biography.publisher.toUpperCase();

  const appearance = data.appearance

  return (
    <>
    <Layout>
    <Head>
      <title>{_.startCase(data.name)} | Superhero Wiki</title>
    </Head>
  
  <div className="row">
  <div className="col-6">
  <h1>{name}</h1>
  <h3>{fullName}</h3>
  <PowerstatsContainer stats={data.powerstats}/>

  asd
  </div>
  
  <div className="col-6">
  <div className="d-flex justify-content-center">
  <img src={data.images.md}/>
  </div>
  <div className="d-flex justify-content-center flex-column align-items-center">
  <p>{appearance.gender}•{appearance.race}</p>
  <p>Height: {appearance.height[0]}/{appearance.height[1]}</p>
  <p>Weight: {appearance.weight[0]}/{appearance.weight[1]}</p>
  </div>
  </div>
  
  
  
  </div>
  </Layout>
  </>
  )

  

}

