import Layout from '../components/Layout'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
// import PowerstatsContainer from '../components/PowerstatsContainer'
import CharacterCard from '../components/CharacterCard'

let currData = []
let num = 30
//no 501 entry
const Index = ({data}) => {
  // console.log(currData)
  return (
  <>
  <Layout>
  
  {/* <Link href="/characters/[id]" as="/characters/502-one-punch-man"><a>One Punch Man</a></Link> */}
  <div className="row no-gutters">
  <div className="col"></div>
  <div className="col-10 p-0">
  <div className="cardGrid">
  {data.map(character => (<CharacterCard data={character}/>))}
  </div>
  </div>
  <div className="col p-0"></div>
  </div>
  </Layout>
  </>
)
  }


async function getInitialProps({ query, res }) {
  currData = []

  for (let i = 1; i < num; i++) {

    try {
      let resp = await fetch(
        `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${i}.json`
      );
      let data = await resp.json();
      currData.push(data)
    } catch (error) {
      console.log(`Error! on ${i}`)
    }
    
  }
  

  // if (query.slug === data.slug) {
  //   return { data, statusCode: 200 };
  // }
  // res.statusCode = 404;
  // return { data: {}, statusCode: 404 };
  return {
    data: currData,
    statusCode: true ? 200 : 404
  }
}

Index.getInitialProps = getInitialProps;


export default Index;
