import { useRouter } from 'next/router'
import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'


const Hero = () => {
  const router = useRouter()
  const { slug } = router.query
  console.log(slug.split('-')[0])

  return (
    <>
  <p>{slug}</p>
  </>
  )
}

export default Hero
