import { createContext, useEffect, useState } from 'react';
import fetch from 'isomorphic-unfetch'

const CharacterContext = createContext()

export function CharacterContextProvider({ children }) {
  const [characters, setCharacters] = useState([])
  useEffect(() => {
    async function fetchData() {
      let resp = await fetch(
        // `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/502.json`
        `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/all.json`
      );
      let data = await resp.json();
      setCharacters(data)
      console.log(data)
    }
    fetchData()
  }, [])


  return (
    <CharacterContextProvider value={{characters}}>
      {children}
    </CharacterContextProvider>
  )

}

export default CharacterContext;
