import Link from "next/link";
import fetch from "isomorphic-unfetch";


const CharacterCard = ({ data }) => {
  const name = data.name.toUpperCase();
  const fullName = data.biography.fullName.toUpperCase();
  const publisher = data.biography.publisher.toUpperCase();

  return (
    <>
      <Link href="/characters/[id]" as={`/characters/${data.slug}`}>
      <div className="characterCard">
        <div className="characterImageContainer">
          <img
            src={data.images.sm}
            alt={data.name}
            className="characterImage"
          />
        </div>

        <div className="info">
          <p className="pl-2 name">{name}</p>
          <p className="pl-2 text-muted full-name">
            
              {name === fullName ? "" : fullName}
            
          </p>
          <p className="pl-2 text-muted">
            
              {publisher}
            
          </p>
        </div>
      </div>
      </Link>
    </>
  );
};

async function getInitialProps({ query, res }) {
  const id = query.slug.split("-", 1)[0];
  const resp = await fetch(
    `https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${id}.json`
  );
  const data = await resp.json();

  if (query.slug === data.slug) {
    return { data, statusCode: 200 };
  }
  res.statusCode = 404;
  return { data: {}, statusCode: 404 };
  // return {
  //   data: data || {},
  //   statusCode: data ? 200 : 404
  // }
}

CharacterCard.getInitialProps = getInitialProps;

export default CharacterCard;




