const PowerBar = ({width, percent}) => {

  const [value, setValue] = React.useState(0)

  React.useEffect(() => {
    setValue(percent/100 * width);
  });

  return (
    <div className="power-bar" style={{width: width}}>
      <div className="level" style={{width: `${value}px`}}></div>
      
    </div>
  )
}

export default PowerBar
