import { useRouter } from 'next/router'
import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'
import PowerstatsContainer from '../../components/PowerstatsContainer'


const Hero = ({heroData}) => {
  const router = useRouter()
  const { slug } = router.query
  // console.log(slug.split('-', 1))
  // const id = slug.split('-', 1)[0]
  
  return (
    <>
  {/* <p>{slug}</p> */}
  <PowerstatsContainer stats={heroData.powerstats}/>
  </>
  )
}

export async function getStaticProps() {
  const res = await fetch(`https://cdn.rawgit.com/akabab/superhero-api/0.2.0/api/id/${502}.json`)
  const heroData = await res.json()
  return {
    props: {
      heroData,
    }
  }
}

export async function getStaticPaths() {
  return {
    paths: [
      { params: { slug: '502-wtf'} }
    ],
    fallback: false
  }
}

export default Hero
