const CharacterCard = ({ data }) => {

  const handleMouseEnter = (e) => {
    // e.target.getElementById('characterImage').style.transform = scale(1.5)
    console.log(e.target)
  }

  const handleMouseLeave = (e) => {

  }
  return (
    <>
      {/* <Link href="/characters/[id]" as="/characters/502-one-punch-man"> */}
      {/* <div className="characterCard" 
    style={{ 
      backgroundImage: `url(${data.images.sm})`,
      
      }}> */}
      <div className="characterCard" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
        <div className="characterImageContainer">
          
            <img
              src={data.images.sm}
              alt={data.name}
              className="characterImage"
              id="characterImage"
            />
          
        </div>
       
        <div className="info">
        <p className="pl-2 name">{data.name.toUpperCase()}</p>
        <p className="pl-2 text-muted full-name">{data.biography.fullName.toUpperCase()}</p>
        <p className="pl-2 text-muted">{data.biography.publisher.toUpperCase()}asdasdasdasdasd</p>
        </div>
      </div>
      {/* </div> */}
      {/* </Link> */}
    </>
  );
};

export default CharacterCard;
